CREATE OR REPLACE FUNCTION fa_obter_regra_receita_atual ( cd_estabelecimento_p bigint, cd_perfil_p bigint ) RETURNS bigint AS $body$
DECLARE


nr_seq_regra_w		bigint;


BEGIN

SELECT	max(a.nr_sequencia)
into STRICT	nr_seq_regra_w
FROM	FA_REGRA_GERACAO_RECEITA a,
	FA_REGRA_GER_REC_PERFIL b
WHERE	a.nr_sequencia		= b.nr_seq_regra
AND	a.cd_estabelecimento 	= cd_estabelecimento_p
AND	a.ie_situacao 	   	= 'A'
AND	b.cd_perfil	   	= coalesce(cd_perfil_p,b.cd_perfil)
AND	clock_timestamp() BETWEEN a.dt_vigencia_ini AND fim_dia(a.dt_vigencia_fim);

if (coalesce(nr_seq_regra_w::text, '') = '') then
	SELECT	max(a.nr_sequencia)
	into STRICT	nr_seq_regra_w
	FROM	FA_REGRA_GERACAO_RECEITA a
	WHERE	a.cd_estabelecimento 	= cd_estabelecimento_p
	AND	a.ie_situacao 	   	= 'A'
	AND	clock_timestamp() BETWEEN a.dt_vigencia_ini AND fim_dia(a.dt_vigencia_fim);
end if;

if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
  return nr_seq_regra_w;
end if;

return null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
