CREATE OR REPLACE FUNCTION fa_obter_retorno_paciente_pmc ( nr_seq_pmc_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w	timestamp;


BEGIN

if (nr_seq_pmc_p IS NOT NULL AND nr_seq_pmc_p::text <> '') then

	SELECT	max(b.dt_prevista_retorno)
	into STRICT	dt_retorno_w
	FROM   	fa_paciente_entrega a,
		fa_entrega_medicacao b,
		fa_paciente_pmc c
	WHERE  	a.nr_sequencia  	  = b.nr_seq_paciente_entrega
	AND	a.cd_pessoa_fisica 	  = c.cd_pessoa_fisica
	AND    	c.nr_sequencia		  = nr_seq_pmc_p;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
