CREATE OR REPLACE FUNCTION fa_obter_saldo_disponivel ( nr_seq_medic_item_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w	integer;


BEGIN

if (nr_seq_medic_item_p IS NOT NULL AND nr_seq_medic_item_p::text <> '') then

	select	qt_gerado - qt_dispensar
	into STRICT	qt_retorno_w
	from	fa_entrega_medicacao_item
	where	nr_sequencia = nr_seq_medic_item_p;

	if (qt_retorno_w < 0) then
		qt_retorno_w := 0;
	end if;

end if;

return	qt_retorno_w;

end;
$body$
