CREATE OR REPLACE FUNCTION fa_obter_se_exibe_mat (cd_material_p bigint, cd_medico_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_exibe_w	varchar(1);
ie_liberacao_w	varchar(1);


BEGIN 
 
ie_liberacao_w	:= Obter_Valor_Param_Usuario(10015, 74, Obter_perfil_Ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento);
 
select	coalesce(max('S'),'N') 
into STRICT	ie_exibe_w 
from	fa_medic_farmacia_amb b, 
	fa_regra_receita_medico a 
where	a.nr_seq_medic = b.nr_sequencia 
and	b.cd_material = cd_material_p;
 
if (ie_exibe_w = 'S') then 
	if (ie_liberacao_w = 'S') then 
	 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_exibe_w 
		from	fa_medic_farmacia_amb b, 
			fa_regra_receita_medico a 
		where	a.nr_seq_medic = b.nr_sequencia 
		and	coalesce(a.cd_medico,cd_medico_p) = cd_medico_p 
		and (coalesce(a.cd_especialidade::text, '') = '' or obter_se_especialidade_medico(cd_medico_p, a.cd_especialidade) = 'S') 
		and	coalesce(a.cd_perfil,Obter_perfil_Ativo) = Obter_perfil_Ativo 
		and	b.cd_material = cd_material_p;
		 
	else 
	 
		select	coalesce(max('N'),'S') 
		into STRICT	ie_exibe_w 
		from	fa_medic_farmacia_amb b, 
			fa_regra_receita_medico a 
		where	a.nr_seq_medic = b.nr_sequencia 
		and	coalesce(a.cd_medico,cd_medico_p) = cd_medico_p 
		and (coalesce(a.cd_especialidade::text, '') = '' or obter_se_especialidade_medico(cd_medico_p, a.cd_especialidade) = 'S') 
		and	coalesce(a.cd_perfil,Obter_perfil_Ativo) = Obter_perfil_Ativo 
		and	b.cd_material = cd_material_p;
		 
	end if;
else 
	ie_exibe_w := 'S';
end if;
 
return	ie_exibe_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
