CREATE OR REPLACE FUNCTION fa_obter_se_exibe_prot (nr_seq_protocolo_p bigint, cd_medico_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_exibe_w	varchar(1);
ie_liberacao_w	varchar(1);


BEGIN 
 
select	coalesce(max('N'),'S') 
into STRICT	ie_exibe_w 
from	fa_protocolo_medic b, 
	fa_regra_protocolo a 
where	a.nr_seq_protocolo = b.nr_sequencia 
and	b.nr_sequencia = nr_seq_protocolo_p;
 
if (ie_exibe_w = 'N') then 
 
	select	coalesce(max('S'),'N') 
	into STRICT	ie_exibe_w 
	from	fa_protocolo_medic b, 
		fa_regra_protocolo a 
	where	a.nr_seq_protocolo = b.nr_sequencia 
	and	coalesce(a.cd_medico,cd_medico_p) = cd_medico_p 
	and (coalesce(a.cd_especialidade::text, '') = '' or obter_se_especialidade_medico(cd_medico_p, a.cd_especialidade) = 'S') 
	and	coalesce(a.cd_perfil,obter_perfil_ativo) = obter_perfil_ativo 
	and	b.nr_sequencia = nr_seq_protocolo_p;
 
end if;
 
return	ie_exibe_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
