CREATE OR REPLACE FUNCTION fa_obter_se_existe_med_receita (nr_seq_receita_p bigint, cd_material_p bigint, nr_seq_farm_item_p bigint default 0) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);

BEGIN

select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_retorno_w
from   	fa_receita_farmacia_item
where  	cd_material 	 = cd_material_p
and	((nr_sequencia 	 <> nr_seq_farm_item_p) or (nr_seq_farm_item_p = 0))
and    	nr_seq_receita 	 = nr_seq_receita_p;

return	ie_retorno_w;

end;
$body$
