CREATE OR REPLACE FUNCTION fa_obter_se_existe_pmc ( nr_seq_receita_p bigint, nr_seq_entrega_origem_p bigint) RETURNS varchar AS $body$
DECLARE


ie_existe_pmc_w	varchar(1);


BEGIN

if (nr_seq_entrega_origem_p IS NOT NULL AND nr_seq_entrega_origem_p::text <> '') then

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_existe_pmc_w
	from	fa_paciente_pmc
	where	nr_entrega_origem_pmc =nr_seq_entrega_origem_p;
elsif (nr_seq_receita_p IS NOT NULL AND nr_seq_receita_p::text <> '') then

	SELECT 	CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT 	ie_existe_pmc_w
	FROM   	fa_entrega_medicacao a
	WHERE  	a.nr_seq_receita_amb = nr_seq_receita_p
	AND    	NOT EXISTS ( SELECT 1
			     FROM   fa_paciente_pmc x
			     WHERE  x.nr_entrega_origem_pmc = a.nr_sequencia);
end if;


return	ie_existe_pmc_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
