CREATE OR REPLACE FUNCTION fa_obter_se_medic_liberado ( nr_seq_medic_rotina_p bigint, cd_medico_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(1);
				

BEGIN 
 
select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END  
into STRICT	ds_retorno_w 
from	fa_medic_rotina_espec 
where	nr_seq_medic_rotina = nr_seq_medic_rotina_p;
 
if (ds_retorno_w = 'N') then 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ds_retorno_w 
	from	fa_medic_rotina_espec 
	where	nr_seq_medic_rotina = nr_seq_medic_rotina_p 
	and	obter_se_especialidade_medico(cd_medico_p,cd_especialidade) = 'S';
end if;
 
if (ds_retorno_w = 'S') then 
	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END  
	into STRICT	ds_retorno_w 
	from	fa_medic_rotina_perfil 
	where	nr_seq_medic_rotina = nr_seq_medic_rotina_p;
 
	if (ds_retorno_w = 'N') then 
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ds_retorno_w 
		from	fa_medic_rotina_perfil 
		where	nr_seq_medic_rotina = nr_seq_medic_rotina_p 
		and	cd_perfil = obter_perfil_ativo;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
