CREATE OR REPLACE FUNCTION fa_obter_se_medic_pac_pmc (nr_seq_fa_entrega_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_medic_w		bigint;
cd_pessoa_fisica_w	varchar(10);
ie_medic_pmc_w		varchar(1);
ie_retorno_w		varchar(1) := 'N';
ie_entrega_pmc_w	varchar(2)  := 'N';


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (nr_seq_fa_entrega_p IS NOT NULL AND nr_seq_fa_entrega_p::text <> '') then


	SELECT 	CASE WHEN coalesce(nr_seq_paciente_pmc::text, '') = '' THEN 'N'  ELSE 'S' END
	into STRICT	ie_entrega_pmc_w
	FROM 	fa_paciente_entrega b,
		fa_entrega_medicacao a
	WHERE 	b.nr_sequencia = a.nr_seq_paciente_entrega
	AND	a.nr_sequencia = nr_seq_fa_entrega_p;

	if (ie_entrega_pmc_w = 'S') then

		select	max(cd_pessoa_fisica)
		into STRICT	cd_pessoa_fisica_w
		from	fa_entrega_medicacao
		where	nr_sequencia	= nr_seq_fa_entrega_p;

		select	max(nr_sequencia),
			max(coalesce(ie_medicamento_pmc,'N'))
		into STRICT	nr_seq_medic_w,
			ie_medic_pmc_w
		from	fa_medic_farmacia_amb
		where	cd_material	= cd_material_p;
	end if;

end if;

if (nr_seq_medic_w IS NOT NULL AND nr_seq_medic_w::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_retorno_w
	from	fa_paciente_pmc a,
		fa_paciente_pmc_medic b
	where	b.nr_seq_fa_pmc		= a.nr_sequencia
	and	a.cd_pessoa_fisica 	= cd_pessoa_fisica_w
	and	b.nr_seq_medic_fa 	= nr_seq_medic_w
	and	coalesce(a.dt_saida,clock_timestamp())	>= clock_timestamp()
	and	ie_medic_pmc_w = 'S';

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
