CREATE OR REPLACE FUNCTION fa_obter_se_medic_pmc ( nr_seq_pmc_p bigint, cd_medicamento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);

BEGIN

SELECT 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_retorno_w
FROM   	fa_paciente_pmc b,
	fa_paciente_pmc_medic a
WHERE  	b.nr_sequencia = a.nr_seq_fa_pmc
and	b.nr_sequencia = nr_seq_pmc_p
AND	fa_obter_dados_medicamento(a.nr_seq_medic_fa, 'M') = cd_medicamento_p;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
