CREATE OR REPLACE FUNCTION fa_obter_se_pac_pmc (cd_pessoa_fisica_p text, nr_seq_entrega_p bigint) RETURNS varchar AS $body$
DECLARE


qt_medic_w	bigint;
qt_pmc_w	bigint;

ie_retorno_w	varchar(1);


BEGIN

select	count(*)
into STRICT	qt_medic_w
from	fa_entrega_medicacao_item a,
	fa_medic_farmacia_amb b
where	a.cd_material			= b.cd_material
and	a.nr_seq_fa_entrega		= nr_seq_entrega_p
and	coalesce(b.ie_medicamento_pmc,'N')	= 'S'
and	coalesce(dt_cancelamento::text, '') = '';

select	count(*)
into STRICT	qt_pmc_w
from	fa_paciente_pmc a,
	fa_paciente_pmc_medic b
where	b.nr_seq_fa_pmc		= a.nr_sequencia
and	a.cd_pessoa_fisica 	= cd_pessoa_fisica_p
and	b.nr_seq_medic_fa in (	SELECT	y.nr_sequencia
				from	fa_entrega_medicacao_item x,
					fa_medic_farmacia_amb y
				where	y.cd_material			= x.cd_material
				and	x.nr_seq_fa_entrega		= nr_seq_entrega_p
				and	coalesce(y.ie_medicamento_pmc,'N') 	= 'S'
				and	coalesce(x.dt_cancelamento::text, '') = '');

if (qt_pmc_w = qt_medic_w) then
	ie_retorno_w	:= 'S';
else
	ie_retorno_w	:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
