CREATE OR REPLACE FUNCTION fa_obter_se_possui_retorno_aut (nr_seq_entrega_medic_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE



dt_validade_receita_w	timestamp;
qt_valida_w		bigint;
ds_retorno_w		varchar(1) := 'N';



BEGIN
if (nr_seq_entrega_medic_p IS NOT NULL AND nr_seq_entrega_medic_p::text <> '') then
	select	c.dt_validade_receita
	into STRICT	dt_validade_receita_w
	from   	fa_entrega_medicacao a,
		fa_receita_farmacia c
	where  	c.nr_sequencia     = a.nr_seq_receita_amb
	and    	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and    	a.nr_sequencia 	   = nr_seq_entrega_medic_p;


	select 	count(*)
	into STRICT	qt_valida_w
	from   	fa_entrega_medicacao a,
		agenda_consulta b
	where  	a.nr_sequencia     = b.nr_seq_fa_entrega
	and    	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and    	a.nr_sequencia 	   <> nr_seq_entrega_medic_p
	and    	a.dt_periodo_inicial between  inicio_dia(clock_timestamp()) and fim_dia(clock_timestamp())
	and    	b.dt_agenda <= dt_validade_receita_w;


	if (qt_valida_w > 0) then
		ds_retorno_w	:= 'S';
	else
		ds_retorno_w	:= 'N';
	end if;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
