CREATE OR REPLACE FUNCTION fa_obter_se_saldo_pendente ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
E - Entrega
M - Medicamento
*/
ds_retorno_w	varchar(1);


BEGIN

if (ie_opcao_p = 'E') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	fa_entrega_medicacao_item
	where	qt_dispensar < qt_gerado
	and	nr_seq_fa_entrega = nr_sequencia_p;

elsif (ie_opcao_p = 'M') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	fa_entrega_medicacao_item
	where	qt_dispensar < qt_gerado
	and	nr_sequencia = nr_sequencia_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
