CREATE OR REPLACE FUNCTION fa_obter_tipo_entrega_pac (nr_seq_paciente_pmc_p bigint) RETURNS varchar AS $body$
DECLARE

ie_tipo_entrega_w	varchar(3);

BEGIN
if (nr_seq_paciente_pmc_p IS NOT NULL AND nr_seq_paciente_pmc_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'PNC' END
	into STRICT	ie_tipo_entrega_w
	from	fa_paciente_pmc a,
		fa_paciente_pmc_medic b,
		fa_medic_farmacia_amb c
	where	a.nr_sequencia = nr_seq_paciente_pmc_p
	and	a.nr_sequencia = b.nr_seq_fa_pmc
	and	c.nr_sequencia = b.nr_seq_medic_fa
	and	coalesce(c.ie_nutricao,'N') = 'S';

	if (ie_tipo_entrega_w = 'N') then
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'PMC' END
		into STRICT	ie_tipo_entrega_w
		from	fa_paciente_pmc a,
			fa_paciente_pmc_medic b,
			fa_medic_farmacia_amb c
		where	a.nr_sequencia = nr_seq_paciente_pmc_p
		and	a.nr_sequencia = b.nr_seq_fa_pmc
		and	c.nr_sequencia = b.nr_seq_medic_fa
		and	coalesce(c.ie_medicamento_pmc,'N') = 'S';
	end if;

	if (ie_tipo_entrega_w = 'N') then
		ie_tipo_entrega_w := 'PMC';
	end if;
else
	ie_tipo_entrega_w := 'N';
end if;
return	ie_tipo_entrega_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
