SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION fa_obter_tipo_entrega (nr_seq_entrega_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_entrega_w	varchar(3);
ie_paciente_pmc_w	varchar(1);


BEGIN

ie_tipo_entrega_w	:= 'N';

if (nr_seq_entrega_p IS NOT NULL AND nr_seq_entrega_p::text <> '') then

	SELECT 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'PNC' END
	into STRICT	ie_tipo_entrega_w
	FROM 	fa_paciente_entrega b,
		FA_ENTREGA_MEDICACAO a,
		fa_paciente_pmc_medic c,
		fa_medic_farmacia_amb d,
		fa_paciente_pmc e,
		fa_entrega_medicacao_item f
	WHERE 	b.nr_sequencia = a.NR_SEQ_PACIENTE_ENTREGA
	AND	a.nr_sequencia = nr_seq_entrega_p
	and	e.nr_sequencia = b.nr_seq_paciente_pmc
	and	e.nr_sequencia = c.nr_seq_fa_pmc
	and	d.nr_sequencia = c.nr_seq_medic_fa
	and	f.nr_seq_fa_entrega = a.nr_sequencia
	and	f.cd_material = d.cd_material
	and	coalesce(d.ie_nutricao,'N') = 'S';

	if (ie_tipo_entrega_w = 'N') then

		SELECT 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'PMC' END
		into STRICT	ie_tipo_entrega_w
		FROM 	fa_paciente_entrega b,
			FA_ENTREGA_MEDICACAO a,
			fa_paciente_pmc_medic c,
			fa_medic_farmacia_amb d,
			fa_paciente_pmc e,
			fa_entrega_medicacao_item f
		WHERE 	b.nr_sequencia = a.NR_SEQ_PACIENTE_ENTREGA
		AND	a.nr_sequencia = nr_seq_entrega_p
		and	e.nr_sequencia = b.nr_seq_paciente_pmc
		and	e.nr_sequencia = c.nr_seq_fa_pmc
		and	d.nr_sequencia = c.nr_seq_medic_fa
		and	f.nr_seq_fa_entrega  = a.nr_sequencia
		and	f.cd_material = d.cd_material
		and	coalesce(d.ie_medicamento_pmc,'N') = 'S';


	end if;

	if (ie_tipo_entrega_w = 'N') then -- para os registros antigos antes da alteração para ter dois registros na pasta PMC/PNC
		SELECT 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'PNC' END
		into STRICT	ie_tipo_entrega_w
		FROM 	fa_paciente_entrega b,
			FA_ENTREGA_MEDICACAO a,
			
			fa_medic_farmacia_amb d,
			fa_paciente_pmc e,
			fa_entrega_medicacao_item f
		WHERE 	b.nr_sequencia = a.NR_SEQ_PACIENTE_ENTREGA
		AND	a.nr_sequencia = nr_seq_entrega_p
		and	e.nr_sequencia = b.nr_seq_paciente_pmc
		--and	e.nr_sequencia = c.nr_seq_fa_pmc
		--and	d.nr_sequencia = c.nr_seq_medic_fa
		and	f.nr_seq_fa_entrega = a.nr_sequencia
		and	f.cd_material = d.cd_material
		and	coalesce(d.ie_nutricao,'N') = 'S';

		if (ie_tipo_entrega_w = 'N') then

			SELECT 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'PMC' END
			into STRICT	ie_tipo_entrega_w
			FROM 	fa_paciente_entrega b,
				FA_ENTREGA_MEDICACAO a,
				
				fa_medic_farmacia_amb d,
				fa_paciente_pmc e,
				fa_entrega_medicacao_item f
			WHERE 	b.nr_sequencia = a.NR_SEQ_PACIENTE_ENTREGA
			AND	a.nr_sequencia = nr_seq_entrega_p
			and	e.nr_sequencia = b.nr_seq_paciente_pmc
			--and	e.nr_sequencia = c.nr_seq_fa_pmc
			--and	d.nr_sequencia = c.nr_seq_medic_fa
			and	f.nr_seq_fa_entrega  = a.nr_sequencia
			and	f.cd_material = d.cd_material
			and	coalesce(d.ie_medicamento_pmc,'N') = 'S';


		end if;
	end if;

end if;

return	ie_tipo_entrega_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION fa_obter_tipo_entrega (nr_seq_entrega_p bigint) FROM PUBLIC;

