CREATE OR REPLACE FUNCTION fa_obter_unid_medida_saldo (nr_seq_entrega_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(30);


BEGIN

if (nr_seq_entrega_p IS NOT NULL AND nr_seq_entrega_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select	max(c.cd_unidade_medida)
	into STRICT	ds_retorno_w
	from	fa_entrega_medicacao a,
		fa_receita_farmacia b,
		fa_receita_farmacia_item c
	where	a.nr_seq_receita_amb 	= c.nr_seq_receita
	and	b.nr_sequencia 		= c.nr_seq_receita
	and	a.nr_sequencia 		= nr_seq_entrega_p
	and	c.cd_material 		= cd_material_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
