CREATE OR REPLACE FUNCTION fa_valida_quantidade_medica (nr_seq_medic_item_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(10);
qt_total_item_w	bigint;
qt_dispensar_w	bigint;


BEGIN

select	coalesce(sum(qt_item),0)
into STRICT	qt_total_item_w
from	fa_entrega_medic_item_lote
where	nr_seq_medic_item =  nr_seq_medic_item_p;

select	qt_dispensar
into STRICT	qt_dispensar_w
from	fa_entrega_medicacao_item
where	nr_sequencia = nr_seq_medic_item_p;

if (qt_total_item_w <> 0) then
	if (qt_total_item_w = qt_dispensar_w) then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
