CREATE OR REPLACE FUNCTION find_keyword ( ds_keywords_p text, ds_search_p text) RETURNS varchar AS $body$
DECLARE


word_list_w	dbms_sql.varchar2_table;
keyword_list_w	dbms_sql.varchar2_table;
BEGIN

if (ds_search_p IS NOT NULL AND ds_search_p::text <> '') and (ds_keywords_p IS NOT NULL AND ds_keywords_p::text <> '') then

	word_list_w := obter_lista_string(lower(ds_search_p), ' ');
	keyword_list_w := obter_lista_string(trim(both lower(ds_keywords_p)), ';');

	 for 	i in word_list_w.first..word_list_w.last loop

		for 	u in keyword_list_w.first..keyword_list_w.last loop
			if (keyword_list_w(u) like '%' || word_list_w(i) || '%') then
				return 'S';
			end if;
		end loop;

	end loop;

end if;

return	'N';

end;
$body$
LANGUAGE PLPGSQL
