SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION find_value_in_string ( nr_valor_chave_p bigint, ds_texto_p text, ie_separador_p text) RETURNS varchar AS $body$
DECLARE



x					dbms_sql.number_table;
ds_texto_w				varchar(32767);
pos_w					bigint;
ie_achou_w				varchar(1)	:= 'N';
BEGIN

begin
	ds_texto_w	:= replace(ds_texto_p,' ','');
	ds_texto_w	:= replace(ds_texto_w,chr(13),'');
	ds_texto_w	:= replace(ds_texto_w,chr(10),'');
exception when others then
	ds_texto_w	:= '';
end;

if (ds_texto_w IS NOT NULL AND ds_texto_w::text <> '') then
	begin
	while(ds_texto_w IS NOT NULL AND ds_texto_w::text <> '') loop
		begin
		pos_w	:= coalesce(position(ie_separador_p in ds_texto_w),0);
		if (pos_w = 0) then
			begin
			x(x.count+1)	:= (substr(ds_texto_w,1,255))::numeric;
			ds_texto_w	:= null;
			exception when others then
				exit;
			end;
		else
			begin
			x(x.count+1)	:= (substr(ds_texto_w,1,pos_w-1))::numeric;
			ds_texto_w	:= substr(ds_texto_w,pos_w+1,32767);
			exception when others then
				exit;
			end;
		end if;
		end;
	end loop;
	
	if (x.count>0) then
		
		for i in 1..x.count loop
			if (x(i) = nr_valor_chave_p) then
				ie_achou_w	:= 'S';
				exit;
			end if;
		end loop;
	end if;
	
	end;
end if;
x.delete;
return ie_achou_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
