CREATE OR REPLACE FUNCTION fin_obter_cheques_caixa_rec ( nr_seq_caixa_rec_p movto_trans_financ.nr_seq_caixa_rec%type ) RETURNS varchar AS $body$
DECLARE


ds_cheques_caixa_rec_w varchar(255) := '';

  vet RECORD;

BEGIN

for vet in (    SELECT	a.nr_cheque
                from	cheque_cr a
                where	a.nr_seq_caixa_rec = nr_seq_caixa_rec_p) loop

                begin
                ds_cheques_caixa_rec_w	:= substr(vet.nr_cheque || ', ' || ds_cheques_caixa_rec_w, 1, 255);
                end;
end loop;

return ds_cheques_caixa_rec_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
