SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION fin_obter_cheque_bordero_titpa (nr_bordero_p bigint, nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


nr_cheque_pago_w varchar(255);

  rec RECORD;

BEGIN

nr_cheque_pago_w        := '';

for rec in (SELECT      a.nr_cheque
            from	cheque_v a
            where	a.nr_sequencia in (       SELECT	b.nr_seq_cheque
                                                from	cheque_bordero_titulo b
                                                where	b.nr_bordero = nr_bordero_p
                                                and	coalesce(b.nr_titulo, coalesce(nr_titulo_p, 0)) = coalesce(nr_titulo_p, 0))
            and	        coalesce(dt_cancelamento::text, '') = '')
loop
        if (length(rec.nr_cheque || '/' || nr_cheque_pago_w) < 255) then
                nr_cheque_pago_w := rec.nr_cheque || '/' || nr_cheque_pago_w;
        end if;
end loop;

return	nr_cheque_pago_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
