CREATE OR REPLACE FUNCTION fin_obter_comprov_movto_ext ( nr_seq_extrato_parcela_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(255);


BEGIN

if (coalesce(nr_seq_extrato_parcela_p,0) <> 0) then

	select	max(c.ds_comprovante)
	into STRICT	ds_retorno_w
	from	movto_cartao_cr c,
		movto_cartao_cr_parcela b,
		extrato_cartao_cr_parcela a
	where	a.nr_sequencia	= b.nr_seq_extrato_parcela
	and	b.nr_seq_movto	= c.nr_sequencia
	and	a.nr_sequencia	= nr_seq_extrato_parcela_p;

	if (coalesce(ds_retorno_w::text, '') = '') then

		select	max(b.ds_comprovante)
		into STRICT	ds_retorno_w
		from	extrato_cartao_cr_arq c,
			extrato_cartao_cr_movto b,
			ext_cartao_cr_movto_concil a
		where	c.ie_tipo_arquivo		<> 'F'
		and	b.nr_seq_extrato_arq		= c.nr_sequencia
		and	a.nr_seq_ext_movto		= b.nr_sequencia
		and	a.nr_seq_extrato_parcela	= nr_seq_extrato_parcela_p;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
