SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION fin_obter_dados_extrato_concil (nr_seq_conciliacao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(4000);
nr_seq_extrato_w			bigint;
dt_inicio_w				timestamp;
dt_final_w				timestamp;


BEGIN

if (coalesce(nr_seq_conciliacao_p,0) <> 0) then
	begin

	select	max(a.nr_sequencia),
		max(a.dt_inicio),
		max(a.dt_final)
	into STRICT	nr_seq_extrato_w,
		dt_inicio_w,
		dt_final_w
	from	banco_extrato a,
		banco_extrato_lanc b,
		concil_banc_movto c
	where	b.nr_seq_extrato	= a.nr_sequencia
	and	c.nr_sequencia		= b.nr_seq_concil
	and	c.nr_seq_conciliacao	= nr_seq_conciliacao_p;

	if (coalesce(nr_seq_extrato_w::text, '') = '') then

		select	max(b.nr_sequencia),
			max(b.dt_inicio),
			max(b.dt_final)
		into STRICT	nr_seq_extrato_w,
			dt_inicio_w,
			dt_final_w
		from	banco_extrato b,
			banco_extrato_lanc a,
			concil_banc_pend_bco c
		where	a.nr_seq_extrato	= b.nr_sequencia
		and	coalesce(a.nr_seq_concil::text, '') = ''
		and	a.ie_conciliacao	= 'N'
		and	c.nr_seq_lanc_extrato	= a.nr_sequencia
		and	c.nr_seq_conciliacao	= nr_seq_conciliacao_p;

	end if;

	if (ie_opcao_p	= 'NRE') then
		ds_retorno_w	:= nr_seq_extrato_w;
	elsif (ie_opcao_p	= 'DIE') and (dt_inicio_w IS NOT NULL AND dt_inicio_w::text <> '') then
		ds_retorno_w	:= to_char(dt_inicio_w,'DD/MM/YYYY');
	elsif (ie_opcao_p	= 'DFE') and (dt_final_w IS NOT NULL AND dt_final_w::text <> '') then
		ds_retorno_w	:= to_char(dt_final_w,'DD/MM/YYYY');
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
