SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION fin_obter_desp_gv ( nr_seq_viagem_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_desp_w		bigint;
vl_relat_w		double precision := '0';
vl_relatorio_w		double precision;
vl_transp_w		double precision;
vl_hosp_w		double precision;
vl_retorno_w		double precision := 0;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	via_relat_desp a
where	a.nr_seq_viagem = nr_seq_viagem_p;


BEGIN
if (ie_opcao_p = 'T') then
	begin
	open C01;
	loop
	fetch C01 into
		nr_seq_desp_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	sum(a.vl_despesa)
		into STRICT	vl_relatorio_w
		from	via_despesa a
		where	a.nr_seq_relat = nr_seq_desp_w
		and	a.ie_responsavel_custo = ie_opcao_p
		and	a.ie_tipo_despesa <> 'T';

		vl_relat_w := vl_relat_w + vl_relatorio_w;
		end;
	end loop;
	close C01;

	select	sum(coalesce(a.vl_reserva, 0))
	into STRICT	vl_transp_w
	from	via_reserva a
	where	a.nr_seq_viagem = nr_seq_viagem_p
	and	a.ie_responsavel_custo = ie_opcao_p
	and	(a.nr_seq_meio_transp IS NOT NULL AND a.nr_seq_meio_transp::text <> '')
	and coalesce(ie_cancelamento, 'X') <> 'C';

	select	sum(coalesce(obter_valor_total_hotel(a.nr_sequencia),0))
	into STRICT	vl_hosp_w
	from	via_reserva a
	where	a.nr_seq_viagem = nr_seq_viagem_p
	and	a.ie_responsavel_custo = ie_opcao_p
	and	(a.nr_seq_hotel IS NOT NULL AND a.nr_seq_hotel::text <> '')
	and coalesce(ie_cancelamento, 'X') <> 'C';

	vl_retorno_w := coalesce(vl_relat_w,0) + coalesce(vl_transp_w,0) + coalesce(vl_hosp_w,0);
	end;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
