CREATE OR REPLACE FUNCTION fin_obter_mtvo_fora_politica ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN
select	substr(max(a.ds_just_7_dias),1,255)
into STRICT	ds_retorno_w
from	fin_gv_pend_aprov a
where	a.nr_seq_viagem = nr_sequencia_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
