CREATE OR REPLACE FUNCTION fin_obter_notas_tit_pag_unif ( nr_titulo_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w				varchar(4000);
nr_retorno_w				varchar(255);

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.nr_nota_fiscal
from	nota_fiscal a,
	titulo_pagar b
where	b.nr_seq_nota_fiscal	= a.nr_sequencia
and	b.nr_titulo_transf	= nr_titulo_p
order by a.nr_sequencia,
	 a.nr_nota_fiscal;

vet01	C01%Rowtype;


BEGIN

ds_retorno_w	:= '';

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ie_opcao_p = 1) then
		nr_retorno_w	:= vet01.nr_sequencia;
	elsif (ie_opcao_p = 2) then
		nr_retorno_w	:= vet01.nr_nota_fiscal;
	end if;

	ds_retorno_w := substr(ds_retorno_w || nr_retorno_w ||', ',1,4000);

	end;
end loop;
close C01;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	ds_retorno_w := substr(ds_retorno_w, 1,instr(ds_retorno_w,',',-1)-1);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
