SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION fin_obter_obs_adiantamento ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_exame_w	varchar(255);
ds_retorno_w	varchar(255);

c01 CURSOR FOR
SELECT	substr(obter_desc_prescr_proc(cd_procedimento,ie_origem_proced, nr_seq_proc_interno),1,240)
from	prescr_procedimento a,
	prescr_medica b
where	a.nr_prescricao = b.nr_prescricao
and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
and	coalesce(b.dt_suspensao::text, '') = ''
and	coalesce(a.ie_suspenso,'N') = 'N'
and	b.nr_atendimento = nr_atendimento_p
group by	cd_procedimento,
	ie_origem_proced,
	nr_seq_proc_interno
order by	1;


BEGIN
open c01;
loop
fetch c01 into
	ds_exame_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_retorno_w := substr(ds_retorno_w || '; ' || ds_exame_w,1,255);
	end;
end loop;
close c01;

ds_retorno_w := substr(ds_retorno_w,3,255);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION fin_obter_obs_adiantamento ( nr_atendimento_p bigint) FROM PUBLIC;

