CREATE OR REPLACE FUNCTION fin_obter_seq_cliente ( cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_cliente_w		bigint;
ds_retorno_w		varchar(4000) := ' and nr_sequencia in (0';

c01 CURSOR FOR
SELECT	distinct
	coalesce(a.nr_seq_cliente,b.nr_seq_cliente)
FROM titulo_receber a
LEFT OUTER JOIN nota_fiscal b ON (a.nr_seq_nf_saida = b.nr_sequencia)
WHERE coalesce(a.vl_saldo_titulo,0) > 0 and (a.cd_estabelecimento = cd_estabelecimento_p or a.cd_estab_financeiro = cd_estabelecimento_p) and substr(obter_se_prod_perfil(a.nr_seq_grupo_prod,1817),1,1) = 'S' and a.ie_situacao not in ('3','5') and coalesce(a.nr_seq_cliente,b.nr_seq_cliente) in (	select	a.nr_sequencia
							from	com_cliente a
							where	a.ie_classificacao = 'EX') and not exists (	select	1
			from	titulo_rec_negociado x
			where	x.nr_titulo = a.nr_titulo) order by	1 asc;

c02 CURSOR FOR
SELECT	distinct
	a.nr_sequencia
from	com_cliente a
where	a.ie_classificacao = 'C';


BEGIN
open C01;
loop
fetch C01 into
	nr_cliente_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w := ds_retorno_w || ',' || nr_cliente_w;
	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_cliente_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	ds_retorno_w := ds_retorno_w || ',' || nr_cliente_w;
	end;
end loop;
close C02;

ds_retorno_w := ds_retorno_w || ') and ie_situacao = ''A'' and ie_classificacao in (''C'',''EX'') ';

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
