CREATE OR REPLACE FUNCTION fin_obter_valor_rep_titulo (nr_seq_titulo_w bigint, nr_seq_nf_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_unitario_material_w	double precision;						

BEGIN 
begin 
select	coalesce(sum(coalesce(a.vl_unitario_material,0)),0) 
into STRICT	vl_unitario_material_w 
from 	ordem_compra_item a, 
	ordem_compra b 
where	a.nr_ordem_compra = b.nr_ordem_compra 
and	b.nr_titulo_receber = nr_seq_titulo_w;
exception 
when others then 
	vl_unitario_material_w := 0;
end;
if (vl_unitario_material_w = 0) then 
	select 	sum(vl_repasse) 
	into STRICT	vl_unitario_material_w 
	from 	nota_fiscal_item_rep x 
	where 	nr_seq_nf_p = x.nr_seq_nf;
end if;
 
return	vl_unitario_material_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
