CREATE OR REPLACE FUNCTION fin_obter_vl_terceiro ( nr_seq_viagem_p bigint) RETURNS bigint AS $body$
DECLARE


vl_total_despesas_w	double precision;


BEGIN
select 	sum(coalesce(a.vl_total_despesas,0))
into STRICT	vl_total_despesas_w
from (
	SELECT 	sum(a.vl_despesa) vl_total_despesas
	from 	via_despesa 	a,
		via_relat_desp 	b
	where	a.nr_seq_relat = b.nr_sequencia
	and 	b.nr_seq_viagem = nr_seq_viagem_p
	and 	a.ie_tipo_despesa <> 'T'
	
union

	SELECT 	sum(coalesce(obter_valor_total_hotel(nr_sequencia),0))  vl_total_despesas
	from 	via_reserva
	where 	nr_seq_viagem = nr_seq_viagem_p
	and 	ie_tipo_reserva = 'H'
	and 	coalesce(ie_cancelamento::text, '') = ''
	
union

	select 	sum(coalesce(vl_reserva,0))  vl_total_despesas
	from 	via_reserva
	where 	nr_seq_viagem = nr_seq_viagem_p
	and 	ie_tipo_reserva = 'T'
	and 	coalesce(ie_cancelamento::text, '') = '') a;

return	vl_total_despesas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
