CREATE OR REPLACE FUNCTION fis_obter_aliq_pis_cofins ( ie_regime_receita_p text, ie_pis_cofins_p text) RETURNS varchar AS $body$
DECLARE




ds_retorno_w		varchar(10);


BEGIN
if (ie_regime_receita_p in ('C','D'))and (ie_pis_cofins_p = 'PIS')then
	ds_retorno_w	:=	'0,65';
else
	ds_retorno_w	:=	'1,65';
end if;

if (ie_regime_receita_p = 'C')and (ie_pis_cofins_p = 'COFINS')then
	ds_retorno_w	:=	'3,00';
elsif (ie_regime_receita_p  = 'N') and (ie_pis_cofins_p   = 'COFINS')then
	ds_retorno_w	:=	'7,60';
elsif (ie_regime_receita_p  = 'D') and (ie_pis_cofins_p   = 'COFINS')then
	ds_retorno_w	:=	'4,00';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
