CREATE OR REPLACE FUNCTION fis_obter_ambulatorio_conta (nr_seq_protocolo_p bigint, nr_interno_conta_p bigint, nr_sequencia_nf_p bigint default 0) RETURNS varchar AS $body$
DECLARE

 
vl_retorno_w		varchar(1) := 'A';
ie_intermediario_w	varchar(1) := 'S';


BEGIN 

if (nr_sequencia_nf_p > 0) then
	select 	fis_obter_se_intermediario(nr_sequencia_nf_p)
	into STRICT	ie_intermediario_w
	;
end if;	

 if (ie_intermediario_w = 'S') then 
	if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') then 
    select	coalesce(max(x.ie_tipo_guia), 'I') 
		into STRICT	vl_retorno_w 
    from 	conta_paciente x 
    where	x.nr_interno_conta = nr_interno_conta_p;
			
    if (vl_retorno_w <> 'I') then
      vl_retorno_w := 'A';
    end if;

	end if;	
else
	vl_retorno_w := 'X';
end if;

return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
