CREATE OR REPLACE FUNCTION fis_obter_cc_ax ( nr_seq_nf_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(50);
cd_local_estoque_w	nota_fiscal_item.cd_local_estoque%type;
cd_centro_custo_nf_w	varchar(50);
cd_centro_custo_le_w	varchar(50);
ie_tipo_local_w		local_estoque.ie_tipo_local%type;


BEGIN

if (nr_seq_nf_p > 0) then

	select 	coalesce(max(a.cd_local_estoque), 0),
		CASE WHEN coalesce(max(a.cd_centro_custo)::text, '') = '' THEN  '12.01.03.045'  ELSE max(a.cd_centro_custo) END
	into STRICT   	cd_local_estoque_w,
		cd_centro_custo_nf_w
	from   	nota_fiscal_item a
	where  	a.nr_sequencia = nr_seq_nf_p
	and 	a.nr_item_nf = 	(SELECT min(nr_item_nf)
				from   	nota_fiscal_item
				where  	nr_sequencia = a.nr_sequencia);

	select 	coalesce(max(ie_tipo_local), 0),
		CASE WHEN coalesce(max(cd_centro_custo)::text, '') = '' THEN  '12.01.03.045'  ELSE max(cd_centro_custo) END
	into STRICT	ie_tipo_local_w,
		cd_centro_custo_le_w
	from	local_estoque
	where 	cd_local_estoque = cd_local_estoque_w;

	if ie_tipo_local_w = 8 then
		ds_retorno_w := cd_centro_custo_nf_w;
	else
		ds_retorno_w := cd_centro_custo_le_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
