SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION fis_obter_dados_grupo_lst_serv (nr_sequencia_p bigint, ie_opcao text) RETURNS varchar AS $body$
DECLARE

 
qt_total_item_w		bigint;
retorno_w		varchar(20);
nr_seq_item_serv_w	bigint;

/* 
LS - LISTA SERVICO 
GS - GRUPO SERVICO 
*/
 
 

BEGIN 
 
select	sum(qt_item) 
into STRICT	qt_total_item_w 
from (SELECT	count(*) qt_item 
	from	nota_fiscal_item a, 
		material_fiscal b 
	where	a.cd_material = b.cd_material	 
	and	a.nr_sequencia = nr_sequencia_p 
	
union all
 
	SELECT	count(*) qt_item 
	from	nota_fiscal_item a, 
		procedimento b 
	where	a.cd_procedimento = b.cd_procedimento 
	and	a.ie_origem_proced = b.ie_origem_proced 
	and	a.nr_sequencia = nr_sequencia_p) alias3;
	 
if (qt_total_item_w > 0) then 
 
	select	max(nr_seq_item_serv) 
	into STRICT	nr_seq_item_serv_w 
	from (SELECT	max(nr_seq_item_serv) nr_seq_item_serv 
		from	nota_fiscal_item a, 
			material_fiscal b 
		where	a.cd_material = b.cd_material	 
		and	a.nr_sequencia = nr_sequencia_p 
		
union all
 
		SELECT	max(nr_seq_item_serv) nr_seq_item_serv 
		from	nota_fiscal_item a, 
			procedimento b 
		where	a.cd_procedimento = b.cd_procedimento 
		and	a.ie_origem_proced = b.ie_origem_proced 
		and	a.nr_sequencia = nr_sequencia_p) alias4;
 
	if (ie_opcao = 'LS') then		 
		select	cd_item_servico 
		into STRICT	retorno_w 
		from	grupo_servico_item 
		where	nr_sequencia = nr_seq_item_serv_w;
	end if;	
	 
	if (ie_opcao = 'GS') then		 
		select	cd_servico 
		into STRICT	retorno_w 
		from	grupo_servico 
		where	nr_sequencia = nr_seq_item_serv_w;
	end if;	
 
end if;
 
return	retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION fis_obter_dados_grupo_lst_serv (nr_sequencia_p bigint, ie_opcao text) FROM PUBLIC;

