CREATE OR REPLACE FUNCTION fis_obter_dados_variacao (nr_seq_variacao_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_dispositivo_fiscal_w	varchar(255);
ds_retorno_w		varchar(255);

BEGIN


select	substr(b.ds_dispostivo_legal,1,255)
into STRICT	ds_dispositivo_fiscal_w
FROM fis_variacao_fiscal a
LEFT OUTER JOIN fis_dispositivo_legal b ON (a.cd_dispositivo_legal = b.nr_sequencia)
WHERE a.nr_sequencia = nr_seq_variacao_p;

if (ie_tipo_p = 'D') then
	ds_retorno_w := ds_dispositivo_fiscal_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
