CREATE OR REPLACE FUNCTION fis_obter_dcomp_darf (nr_seq_darf_p darf.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


cd_processo_dcomp_w	darf.cd_processo_dcomp%type;
ds_retorno_w		varchar(4000);

c_processo_dcomp CURSOR FOR
SELECT	a.cd_processo_dcomp
from	darf_perdcomp a
where	a.nr_seq_darf	= nr_seq_darf_p
order by nr_sequencia;


BEGIN

open c_processo_dcomp;
loop
fetch c_processo_dcomp into
	cd_processo_dcomp_w;
EXIT WHEN NOT FOUND; /* apply on c_processo_dcomp */
	begin
	if (coalesce(ds_retorno_w,'X') = 'X') then
		ds_retorno_w	:= cd_processo_dcomp_w || ', ';
	else
		ds_retorno_w	:= substr(ds_retorno_w || cd_processo_dcomp_w || ', ',1,4000);
	end if;
	end;
end loop;
close c_processo_dcomp;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	ds_retorno_w	:= substr(ds_retorno_w,1,length(ds_retorno_w)-2);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
