CREATE OR REPLACE FUNCTION fis_obter_ecf_total_valor ( cd_estabelecimento_p bigint, nr_seq_estrut_item_p bigint, nr_seq_lote_p bigint) RETURNS bigint AS $body$
DECLARE


ds_termos_w     varchar(4000);
vl_conta_w 	    fis_ecf_valor_contabil_w.vl_conta%type;
ds_operacao_w   fis_ecf_valor_contabil_w.ds_operacao%type;
vl_conta_aux_w 	varchar(4000);

c01 CURSOR FOR
  SELECT  replace(replace(vl_conta,'.',''),',','.') vl_conta,
          replace(replace(ds_operacao,'.',''),',','.') ds_operacao
  from (  SELECT  sum(vl_conta) vl_conta,
                  ds_operacao,
                  max(nr_sequencia) nr_sequencia
          from    fis_ecf_valor_contabil_w
          where   cd_estabelecimento = cd_estabelecimento_p
          and     nr_seq_estrut_item = nr_seq_estrut_item_p
          and     nr_seq_lote = nr_seq_lote_p
          group by cd_conta_superior, ds_operacao  ) alias6
	order by nr_sequencia;
vet01		c01%RowType;


BEGIN
	open C01;
	loop
	fetch C01 into
		vet01;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
      ds_operacao_w := vet01.ds_operacao;
      vl_conta_aux_w := vet01.vl_conta;

      if ((ds_operacao_w = '+' and substr(vl_conta_aux_w, 1, 1) = '+') or (ds_operacao_w = '-' and substr(vl_conta_aux_w, 1, 1) = '-')) then
        ds_operacao_w := '+';
        vl_conta_aux_w := substr(vl_conta_aux_w, 2);
      elsif ((ds_operacao_w = '+' and substr(vl_conta_aux_w, 1, 1) = '-') or (ds_operacao_w = '-' and substr(vl_conta_aux_w, 1, 1) = '+')) then
        ds_operacao_w := '-';
        vl_conta_aux_w := substr(vl_conta_aux_w, 2);
      end if;

			ds_termos_w := ds_termos_w || ds_operacao_w || vl_conta_aux_w;

      if (length(ds_termos_w) >= 3500) then
	      ds_termos_w := Obter_valor_Dinamico('select ' || ds_termos_w || ' from dual', ds_termos_w);
      end if;

		end;
	end loop;
	close C01;

	vl_conta_w := Obter_valor_Dinamico('select ' || ds_termos_w || ' from dual', vl_conta_w);
	return coalesce(vl_conta_w, 0);
	
end;
$body$
