CREATE OR REPLACE FUNCTION fis_obter_natureza_retencao ( cd_cgc_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := null;
cd_tipo_pessoa_w		pessoa_jur_tipo_adic.cd_tipo_pessoa%type;


BEGIN

if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then

	select	max(p.cd_tipo_pessoa)
	into STRICT	cd_tipo_pessoa_w
	from	pessoa_jur_tipo_adic p
	where 	p.cd_cgc = cd_cgc_p;

	if (cd_tipo_pessoa_w IS NOT NULL AND cd_tipo_pessoa_w::text <> '') then

		select 	max(t.ie_nat_retencao_fonte)
		into STRICT	ds_retorno_w
		from 	tipo_pessoa_juridica t
		where 	t.cd_tipo_pessoa = cd_tipo_pessoa_w;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
