CREATE OR REPLACE FUNCTION fis_obter_nbs_procedimento ( cd_area_procedimento_p bigint, cd_especialidade_p bigint, cd_grupo_proc_p bigint, cd_procedimento_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


/*
IE_RETORNO_P
S = Sequência do NBS
C = Código do NBS
*/
ds_retorno_w		varchar(255);
cd_nbs_w		varchar(20);
nr_sequencia_w		bigint;


BEGIN

begin

select	cd_nbs,
	nr_sequencia
into STRICT	cd_nbs_w,
	nr_sequencia_w
from	(
	SELECT	cd_nbs,
		nr_sequencia
	from	lista_nbs a
	where	((obter_se_estrutura_proc(cd_area_procedimento_p, cd_especialidade_p, cd_grupo_proc_p, cd_procedimento_p,
					a.cd_area_procedimento, a.cd_especialidade, a.cd_grupo_proc, a.cd_procedimento) = 'S') or (coalesce(coalesce(a.cd_area_procedimento, a.cd_especialidade, a.cd_grupo_proc, a.cd_procedimento)::text, '') = ''))
	order by
		a.cd_procedimento,
		a.cd_grupo_proc,
		a.cd_especialidade,
		a.cd_area_procedimento) alias6 LIMIT 1;

if (coalesce(ie_retorno_p,'S') = 'S') then
	begin
	ds_retorno_w	:= nr_sequencia_w;
	end;
elsif (coalesce(ie_retorno_p,'S') = 'C') then
	ds_retorno_w	:= cd_nbs_w;
end if;

exception
when others then
	cd_nbs_w	:= '';
	nr_sequencia_w	:= '';
	ds_retorno_w	:= '';
end;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
