CREATE OR REPLACE FUNCTION fis_obter_preco_tabela ( cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


vl_preco_tabela_w		double precision := 0;


BEGIN

if cd_material_p > 0 then
	select	coalesce(Obter_Simpro_Preco_mat(b.nr_sequencia, 1, 1, clock_timestamp()),0) vl_tabela
	into STRICT	vl_preco_tabela_w
	FROM material a
LEFT OUTER JOIN material_simpro b ON (a.cd_material = b.cd_material)
WHERE a.cd_material = cd_material_p and ie_tipo_material in ('1','7','10')
	
union

	SELECT	coalesce(obter_valor_brasindice(a.cd_material,'D'),0)
	from	material a
	where	a.cd_material = cd_material_p
	and	ie_tipo_material in ('0','2','3','6','8','9');
end if;

return	vl_preco_tabela_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
