SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION fis_obter_rfc_convenio (cd_convenio_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(255);
cd_cgc_w		convenio.cd_cgc%type;
cd_cnpj_agencia_w	pessoa_juridica_conta.cd_cnpj_agencia%type;


BEGIN

select 	cd_cgc
into STRICT	cd_cgc_w
from 	convenio
where	cd_convenio = cd_convenio_p;


if	((coalesce(cd_cgc_w,'X') <> 'X') and (ie_opcao_p = 'RFC')) then

	select	a.cd_cnpj_agencia
	into STRICT	cd_cnpj_agencia_w
	from	pessoa_juridica_conta	a
	where	a.cd_cgc = cd_cgc_w;

	if (coalesce(cd_cnpj_agencia_w,'X') <> 'X')then

		select	cd_rfc
		into STRICT	ds_retorno_w
		from 	pessoa_juridica
		where	cd_cgc = cd_cnpj_agencia_w;

	end if;

elsif	((coalesce(cd_cgc_w,'X') <> 'X') and (ie_opcao_p = 'CONTA')) then

	select	a.NR_CONTA
	into STRICT	ds_retorno_w
	from	pessoa_juridica_conta	a
	where	a.cd_cgc = cd_cgc_w;


elsif	((coalesce(cd_cgc_w,'X') <> 'X') and (ie_opcao_p = 'NBANCO')) then

	select	obter_nome_banco(a.cd_banco)
	into STRICT	ds_retorno_w
	from	pessoa_juridica_conta	a,
	        pessoa_juridica b
	where	a.cd_cgc = cd_cgc_w
	and     b.cd_cgc =  a.cd_cnpj_agencia
	and     coalesce(obter_dados_pais(b.nr_seq_pais,'SG'),'MEX') <> 'MEX';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
