CREATE OR REPLACE FUNCTION fis_obter_se_medicamento (cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_medicamento_w		varchar(1) := 'N';


BEGIN

if cd_material_p > 0 then

	select	coalesce(max('S'),'N')
	into STRICT	ie_medicamento_w
	from	material a
	where	a.cd_material = cd_material_p
	and	a.ie_tipo_material in ('0','2','3','6','8','9');

end if;

return	ie_medicamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
