CREATE OR REPLACE FUNCTION fis_obter_situacao_rps ( cd_natureza_operacao_p bigint, cd_estabelecimento_p text, ie_situacao_p bigint) RETURNS varchar AS $body$
DECLARE

				 
ds_retorno_w			varchar(255) := null;


BEGIN 
 
if ie_situacao_p = 9 then 
	ds_retorno_w := 'C';
end if;
 
if coalesce(ds_retorno_w::text, '') = '' then 
	ds_retorno_w := obter_dados_natureza_operacao(cd_natureza_operacao_p,'ISS');
end if;
 
if coalesce(ds_retorno_w::text, '') = '' then 
	case obter_dados_parametro_compras(cd_estabelecimento_p,14) 
	when '1' then 
		ds_retorno_w := 'T';
	when '2' then 
		ds_retorno_w := 'F';
	when '3' then 
		ds_retorno_w := 'I';
	when '5' then 
		ds_retorno_w := 'J';
	else 
		ds_retorno_w := 'I';
	end case;
end if;
 
return	ds_retorno_w;
 
end;
$body$
