CREATE OR REPLACE FUNCTION fis_obter_tipo_correcao (ie_tipo_correcao_p bigint, nr_seq_nota_fiscal_p bigint, dt_liberacao_p text default null) RETURNS varchar AS $body$
DECLARE

ie_X_w  varchar(10);


BEGIN

if (coalesce(dt_liberacao_p::text, '') = '') then

        select  max(CASE WHEN ie_tipo_correcao=ie_tipo_correcao_p THEN 'X' END )
        into STRICT    ie_X_w
        from    fis_carta_correcao
        where   nr_seq_nota_fiscal = nr_seq_nota_fiscal_p
        and     ie_tipo_correcao = ie_tipo_correcao_p;
else
        select  max(CASE WHEN ie_tipo_correcao=ie_tipo_correcao_p THEN 'X' END )
        into STRICT    ie_X_w
        from    fis_carta_correcao
        where   nr_seq_nota_fiscal = nr_seq_nota_fiscal_p
        and     ie_tipo_correcao = ie_tipo_correcao_p
        and     ((coalesce(dt_liberacao_p, '0') = '0') or (to_char(dt_liberacao, 'dd/mm/yyyy') = dt_liberacao_p));

end if;

RETURN ie_X_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
