CREATE OR REPLACE FUNCTION fis_obter_usocfdi (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


cd_uso_cfdi_w		fis_uso_cfdi.cd_uso_cfdi%TYPE;


BEGIN

IF (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') THEN
	BEGIN
		SELECT 	MAX(cd_uso_cfdi)
		INTO STRICT	cd_uso_cfdi_w
		FROM 	fis_uso_cfdi
		WHERE 	nr_seq_nota = nr_sequencia_p;
	EXCEPTION
