CREATE OR REPLACE FUNCTION fis_obter_valor_apo_esp_2010 ( tx_tributo_p bigint, vl_base_calculo_p bigint, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w 		double precision := 0;
ie_opcao_w		smallint;

/* Opções
2- vlrServicos15
3- vlrServicos20
4- vlrServicos25
6- BaseServicos15
7- BaseServicos20
8- BaseServicos25
*/
BEGIN

if (tx_tributo_p > 0) and (vl_base_calculo_p > 0) and (ie_opcao_p > 0) then

	if tx_tributo_p = 15 then

		if ie_opcao_p = 2 then
			vl_retorno_w :=	vl_base_calculo_p * 0.04;
		elsif ie_opcao_p = 6 then
			vl_retorno_w := vl_base_calculo_p;
		end if;

	elsif tx_tributo_p = 14 then

		if ie_opcao_p = 3 then
			vl_retorno_w :=	vl_base_calculo_p * 0.03;
		elsif ie_opcao_p = 7 then
			vl_retorno_w := vl_base_calculo_p;
		end if;

	elsif tx_tributo_p = 13 then

		if ie_opcao_p = 4 then
			vl_retorno_w :=	vl_base_calculo_p * 0.02;
		elsif ie_opcao_p = 8 then
			vl_retorno_w := vl_base_calculo_p;
		end if;

	end if;

end if;

return vl_retorno_w;

end;
$body$
