CREATE OR REPLACE FUNCTION fis_valida_email_concat (ds_email_p text) RETURNS varchar AS $body$
DECLARE


ds_email_w	varchar(2000);
ds_email_ww	varchar(2000);
nr_inicio_w	bigint;
nr_fim_w	bigint;
ie_valido_w	varchar(1);


BEGIN

ds_email_w := ds_email_p;

while(ds_email_w IS NOT NULL AND ds_email_w::text <> '') loop
	begin

	nr_fim_w	:= coalesce(position(';' in ds_email_w),0);

	if (nr_fim_w > 0) and (substr(ds_email_w, length(ds_email_w), 1) <> ';') then
		nr_inicio_w	:= 1;
		ds_email_ww	:= substr(ds_email_w, nr_inicio_w, nr_fim_w);

		ie_valido_w := obter_se_email_valido(substr(ds_email_ww, 1, nr_fim_w - 1));

		if ie_valido_w = 'N' then
			exit;
		end if;

		if (nr_inicio_w > 0) and (nr_fim_w > 0) then
			ds_email_w := substr(replace(ds_email_w, ds_email_ww,''),1,2000);
		end if;
	else
		ie_valido_w := obter_se_email_valido(ds_email_w);
		ds_email_w := null;
	end if;

	end;
end loop;

return ie_valido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
