SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION fis_verificar_compl_vinculado ( nr_sequencia_p fis_dados_compl_import.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


nr_seq_nf_imp_w	fis_dados_compl_import.nr_seq_nf_imp%type;
ds_retorno_w		varchar(1) := 'N';


BEGIN

begin
	select 	coalesce(a.nr_seq_nf_imp, 0)
	into STRICT 	nr_seq_nf_imp_w
	from    fis_dados_compl_import a,
		titulo_pagar b,
		titulo_pagar_baixa c
	where   a.nr_sequencia = nr_sequencia_p
	and     (a.nr_titulo IS NOT NULL AND a.nr_titulo::text <> '')
	and     a.nr_titulo = b.nr_titulo
	and 	b.nr_titulo = c.nr_titulo
	and 	a.nr_seq_tit_pagar_baixa  = c.nr_sequencia
	and	(c.nr_nfe_imp IS NOT NULL AND c.nr_nfe_imp::text <> '')
	and exists (SELECT 1 from nota_fiscal n where n.nr_nfe_imp = a.nr_nfe_imp);
exception
	when others then
		nr_seq_nf_imp_w := 0;
end;

if (nr_seq_nf_imp_w <> 0) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
