CREATE OR REPLACE FUNCTION fleury_obter_seq_int_lab_gr ( nr_prescricao_p bigint, nr_ficha_p text) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w			varchar(1);
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
ie_agrupa_ficha_w		lab_parametro.ie_agrupa_ficha_fleury%type;
nr_grupo_integracao_w	prescR_procedimento.nr_grupo_integracao%type;

c01 CURSOR FOR 
	SELECT	substr(obter_se_int_prescr_fleury_seq(a.nr_prescricao, a.nr_sequencia),1,255) cd 
	from	prescr_procedimento a 
	where	a.nr_prescricao = nr_prescricao_p 
	and		coalesce(nr_grupo_integracao,0) = nr_grupo_integracao_w;

c01_w	c01%rowtype;
	

BEGIN 
 
ie_retorno_w	:= 'N';
 
select	max(a.cd_estabelecimento) 
into STRICT	cd_estabelecimento_w 
from	prescr_medica a 
where	a.nr_prescricao = nr_prescricao_p;
 
select	coalesce(max(a.ie_agrupa_ficha_fleury),'N') 
into STRICT	ie_agrupa_ficha_w 
from	lab_parametro a 
where	a.cd_estabelecimento = cd_estabelecimento_w;
 
if (ie_agrupa_ficha_w = 'S') then 
	select	max(nr_grupo_integracao) 
	into STRICT	nr_grupo_integracao_w 
	from	prescr_procedimento a 
	where	a.nr_prescricao = nr_prescricao_p 
	and		((coalesce(nr_ficha_p,'0') = '0') or (a.nr_controle_ext = nr_ficha_p));
else 
	nr_grupo_integracao_w	:= 0;
end if;
 
open c01;
loop 
fetch c01 into c01_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	if (c01_w.cd <> '0') then 
		ie_retorno_w	:= 'S';	
	end if;
	end;
end loop;
close c01;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
