CREATE OR REPLACE FUNCTION fleury_qt_pend_resp ( nr_prescricao_p bigint) RETURNS bigint AS $body$
DECLARE

 
qt_itens_w	bigint := 0;


BEGIN 
 
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then 
	 
	select	count(*) 
	into STRICT	qt_itens_w 
	from	prescr_procedimento r 
	where	r.nr_prescricao = nr_prescricao_p 
	and		(((obter_se_int_prescr_fleury(r.nr_prescricao) IS NOT NULL AND (obter_se_int_prescr_fleury(r.nr_prescricao))::text <> '')) or ((obter_se_int_pr_fleury_diag(r.nr_prescricao) IS NOT NULL AND (obter_se_int_pr_fleury_diag(r.nr_prescricao))::text <> ''))) 
	and   not exists (SELECT 	1 
						from 	fleury_prescr_perg_tec v, fleury_prescr_perg_resp u 
						where 	v.nr_prescricao = r.nr_prescricao 
						and 	v.nr_seq_prescr = r.nr_sequencia 
						and   v.nr_sequencia = u.nr_seq_perg_tec);
end if;
 
return	qt_itens_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
